mapper.select:
SELECT ID , ACNUMBER , BANKNAME , ACSTATE , ACBALANCE ,ACTYPE , NEWDATE , LASTDATE , TRSFLIMIT
  FROM ACCOUNT_TBL
 WHERE ID = #{id}

mapper.update:
UPDATE CUSTOMER
SET CUSSTATE  = '탈퇴승인',SHOW = 'n'
WHERE id = #{id}

UPDATE CUSTOMER
SET CUSSTATE  = '탈퇴불가능(사유:잔액존재)',SHOW = 'n'
WHERE id = #{id}

UPDATE ACCOUNT_TBL
SET ACBALANCE = ACBALANCE - #{fBalance}
WHERE ACNUMBER = #{acNumber}
