mapper.update:
UPDATE ACCOUNT_TBL 
SET ACBALANCE = ACBALANCE - #{tAmount}
WHERE ACNUMBER = #{acNumber}

UPDATE ACCOUNT_TBL
SET ACBALANCE = ACBALANCE + #{tAmount}
WHERE ACNUMBER = #{acNumber}

update auto_transferReg_tbl
set aState = #{aState}
where aNum = #{aNum}

update auto_transferReg_tbl
set aUpdate = #{aUpdate} + #{aTransferCycle}
where aNum = #{aNum}

mapper.select:
select * from auto_transferReg_tbl
where acNumber = #{acNumber}
  and aState = #{aState}

select * from auto_transferReg_tbl
where acNumber = #{acNumber}
  and aState LIKE '%' || #{aState} || '%'

mapper.update:
update auto_transferReg_tbl
set aState = #{aState}
, aCancelDate = sysdate
where aNum = #{aNum}

update auto_transferReg_tbl
set aDepositAmount = #{aDepositAmount} , 
aTransferCycle = #{aTransferCycle}, 
aEndDate = #{aEndDate}
where aNum = #{aNum}

update account_tbl
set trsfLimit = #{trsfLimit}
where acNumber = #{acNumber}

update notice_tbl
set nTitle = #{nTitle}, nContent = #{nContent}
where nNum = #{nNum}
